/*Table structure for table `noticia_log` */

DROP TABLE IF EXISTS `noticia_log`;

CREATE TABLE `noticia_log` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `id_noticia` int(11) NOT NULL,
  `id_usuario` int(11) NOT NULL,
  `titulo` varchar(100) NOT NULL,
  `texto` text NOT NULL,
  `posicion` int(11) NOT NULL,
  `fecha` datetime NOT NULL,
  `imagen` varchar(100) DEFAULT NULL,
  `estado` int(11) NOT NULL,
  `operacion` varchar(30) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `FK_reference_2` (`id_noticia`),
  KEY `FK_reference_3` (`id_usuario`),
  CONSTRAINT `FK_reference_2` FOREIGN KEY (`id_noticia`) REFERENCES `noticia` (`id`),
  CONSTRAINT `FK_reference_3` FOREIGN KEY (`id_usuario`) REFERENCES `usuario` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=11 DEFAULT CHARSET=utf8;

